mapper.select:
select *
from (
select rownum as rn, notice_number,
notice_title, notice_text, notice_id
from (
select *
from notice, Member
where notice_id = member_id and notice_title like '%'||#{search}||'%'
order by notice_number desc
)
)
where rn >= #{start} and rn <=
#{end}

mapper.update:
update notice
set notice_title = #{notice_title},
notice_text = #{notice_text}, notice_category = #{notice_category}
where notice_number = #{notice_number}

mapper.select:
